import React, { useState } from "react";
import InfiniteScroll from "react-infinite-scroller";
import CircularProgress from "@material-ui/core/CircularProgress";
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import NumericLabel from 'react-pretty-numbers'
import Typography from '@material-ui/core/Typography';
import {Row, Col} from 'react-bootstrap';
import Grid from '@material-ui/core/Grid';
import { Container, Divider } from '@material-ui/core';
import TextTruncate from 'react-text-truncate'; // recommend

const items = new Array(30).fill(0).map((_, index) => ({
  id: index,
  name: `Number ${index}`
}));

function InfiniteScrolls(props) {
  const [loadedIems, setLoadedItems] = useState([]);
  const [offset, setOffset] = useState(0);

  const loadMore = (offset = 0, limit = 10) => {
    if (offset + limit > items.length) {
      return;
    }
    console.log("Loading more");
    setOffset(offset => {
      return offset + limit;
    });
    setLoadedItems(loadedItems => {
      return [...loadedItems, ...items.slice(offset, offset + limit)];
    });
  };

  return (
    <div className="App">
      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
      {console.log(props)}

      <InfiniteScroll
        loadMore={() => loadMore(offset)}
        hasMore={true}
        loader={<CircularProgress />}
        container
        spacing={3}
      >
        {props ? props
                        .map((data)=>(
                            <Grid container style={{  marginTop: '20px ' }}> 
                                <Grid item  xs={3.8}>
                                    <div style={{ width: '260px', height: 'fit-content' }}>
                                        <CardMedia
                                        component="img"
                                        alt={data.title}
                                        height="140"
                                        image={data.thumbnailsHight}
                                        title="Contemplative Reptile"
                                        />
                                    </div>
                                </Grid>
                                <Grid item  xs={8} style={{ marginLeft: '18px' }}>
                                    <CardContent  style={{ padding: '0' }}>
                                        <Typography gutterBottom variant="h6" component="h5">
                                            {data.title}
                                        </Typography>
                                        <Typography gutterBottom variant="caption" component="body1" style={{ textOverflow:  'ellipsis', overflow: 'hidden' }}>
                                            <TextTruncate
                                                line={2}
                                                element="span"
                                                truncateText="â€¦"
                                                text={data.description}
                                            />
                                        </Typography>
                                        <Grid container>
                                            <Grid style={{ marginTop: '20px' }}>
                                                <Row xs={3}>
                                                    <Col xs={1}>
                                                        <Typography variant="body2" color="textSecondary" component="p">
                                                            <NumericLabel params={option}>{data.viewCount}</NumericLabel> 
                                                        </Typography>
                                                    </Col>
                                                    <Col xs={2}>
                                                        <Typography variant="body2" color="textSecondary" component="p">
                                                            views
                                                        </Typography>
                                                    </Col>
                                                </Row>
                                            </Grid>
                                            <Grid  style={{ marginTop: '20px' }}>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    Published  <Moment fromNow>{data.publishedAt}</Moment>
                                                </Typography>
                                            </Grid>
                                        </Grid>
                                </CardContent>          
                                </Grid>
                            </Grid>
                        )):
                            ""
                    }

      </InfiniteScroll>
    </div>
  );
}

export default InfiniteScrolls
