import React, { Component } from 'react';
import {loadYData} from '../../store/actions/youtube'; 
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import store from '../../store/configureStore';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/styles';
import {Row, Col} from 'react-bootstrap'
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';

const PrettyNumber = require('react-pretty-number');

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });


export class RecentVideos extends Component {
    static propTypes = {
        loadYData: PropTypes.func.isRequired,
        isLoading: PropTypes.bool,
        ydata:  PropTypes.array,
        loading: PropTypes.bool,
      };

      componentDidMount(){
        this.props.loadYData()
        console.log(this.props)
      }

    render() {
        const le  = this.props.ydata
        const l2 = le?le.length:""
        const loading = this.props;
        return (
          <div>
            <div>
              <h1>
                Trending Videos
              </h1>
              <Row>
                    {this.props.ydata?this.props.ydata.sort((a,b)=>{
                      return (a.viewCount > b.viewCount) ? 1 : -1 
                    }).reverse()
                    .slice(0,8)
                    .map((data)=>(
                    <Col xs={3}>
                        <div style={{ maxWidth: '325px'}}  elevation={0}>
                            <div>
                                <CardMedia
                                component="img"
                                alt="Contemplative Reptile"
                                height="140"
                                image={data.thumbnailsHight}
                                title="Contemplative Reptile"
                                />
                                <CardContent>
                                <Typography gutterBottom variant="h6" component="h5">
                                    {data.title || <Skeleton />}
                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="p">
                                    {data.channelTitle || <Skeleton />}
                                </Typography>
                                <Grid container spacing={3}>
                                  <Grid item xs={6}>
                                    <Typography variant="body2" color="textSecondary" component="p">
                                    <PrettyNumber number={data.viewCount + " view "  || <Skeleton />  } />
                                      <Moment toNow>{data.publishedAt  || <Skeleton />}</Moment>
                                  </Typography>
                                  </Grid>
                                </Grid>
                                </CardContent>
                            </div>
                        </div>
                    </Col>
                    )):
                      ""
                  } 
                </Row>
            </div>
            <div>
              <h1>
                Recent Videos
              </h1>
            </div>
          </div>
        )
    }
}

const mapStateToProps = (state) => ({
    isLoading: state.youtube.isLoading,
    ydata: state.youtube.ydata,
  });

export default connect(mapStateToProps, {loadYData})(RecentVideos)
