import React, { Component } from 'react'
import Toolbar from '@material-ui/core/Toolbar';
import Link from '@material-ui/core/Link';
import { Divider, Grid } from '@material-ui/core';
import {Container,Image,  Row, Col}  from 'react-bootstrap'
import Typography from '@material-ui/core/Typography';
import {loadBlog} from '../../store/actions/blog';
import { connect } from 'react-redux';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import NumericLabel from 'react-pretty-numbers'
import CardMedia from '@material-ui/core/CardMedia';


const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };

    const data = [
        {
          src:
            'https://i.ytimg.com/vi/pLqipJNItIo/hqdefault.jpg?sqp=-oaymwEYCNIBEHZIVfKriqkDCwgBFQAAiEIYAXAB&rs=AOn4CLBkklsyaw9FxDmMKapyBYCn9tbPNQ',
          title: 'Don Diablo @ Tomorrowland Main Stage 2019 | Officialâ€¦',
          channel: 'Don Diablo',
          views: '396 k views',
          createdAt: 'a week ago',
        },
        {
          src:
            'https://i.ytimg.com/vi/_Uu12zY01ts/hqdefault.jpg?sqp=-oaymwEZCPYBEIoBSFXyq4qpAwsIARUAAIhCGAFwAQ==&rs=AOn4CLCpX6Jan2rxrCAZxJYDXppTP4MoQA',
          title: 'Queen - Greatest Hits',
          channel: 'Queen Official',
          views: '40 M views',
          createdAt: '3 years ago',
        },
        {
          src:
            'https://i.ytimg.com/vi/kkLk2XWMBf8/hqdefault.jpg?sqp=-oaymwEYCNIBEHZIVfKriqkDCwgBFQAAiEIYAXAB&rs=AOn4CLB4GZTFu1Ju2EPPPXnhMZtFVvYBaw',
          title: 'Calvin Harris, Sam Smith - Promises (Official Video)',
          channel: 'Calvin Harris',
          views: '130 M views',
          createdAt: '10 months ago',
        },
      ];
export  class Blog extends Component {

    componentDidMount(){
        this.props.loadBlog()
        console.log(this.props.bdata)
      }
    render() {
            console.log(this.props.bdata)
        return (
           <Grid container direction={'row'} spacing={0}>
            {(this.props.isLoading ? Array.from(new Array(8)) : (this.props.bdata?this.props.bdata:"")).map((item, index) => (
                      <Grid xs={3}  style={{ flexBasis: '24%', cursor: 'pointer', transform: this.props.DrawerStateOpen?"scale(0.90)":"" }} nopadding>
                      <Link href="" onClick={e => this.props.history.push(`/watch?v=${item.videoId}`)}  style={{ textDecoration: 'none', color: 'currentcolor' }}>
                        <Box key={index}  height={260} width={360} marginRight={0.5} my={5}>
                            {item ? (
                              <CardMedia
                                style={{ width: "inherit", height: 200 , objectFit: 'cover' }}                                   
                                image={item.coverImage}
                                title={item.title}>
                              </CardMedia>
                            ) : (
                              <Skeleton variant="rect" width="inherit" height={180} animation={false} style={{ backgroundColor: 'rgba(0, 0, 0, 0.14)' }}/>
                            )}
                          {item ? (
                            <Box pr={2} >
                              <Typography style={{ fontWeight: '500', display: '-webkit-box', WebkitLineClamp: '2', WebkitBoxOrient: 'vertical', overflow: 'hidden',textOverflow:  'hidden' }} gutterBottom variant="h6">
                                {item.title}
                              </Typography>
                            </Box>
                          ) : (
                            <div>
                              <Box pt={0.5}>
                              <Skeleton variant="rect" height={20} width="90%"  animation={false} style={{ backgroundColor: 'rgba(0, 0, 0, 0.14)', marginTop: '10px' }}/>
                              <Skeleton variant="rect" height={20} width="60%" animation={false} style={{ backgroundColor: 'rgba(0, 0, 0, 0.14)', marginTop: '10px' }}/>
                            </Box>
                            </div>
                          )}
                        </Box>
                      </Link>
                    </Grid>
                ))}
           </Grid>
        )
    }
}

const mapStateToProps = (state) => ({
    bdata: state.blog.bdata,
    isLoading: state.blog.isLoading,
  });



export default connect(mapStateToProps, {loadBlog})(Blog)
