                      //           <Card.Body>{data.description}</Card.Body>
                      //         </Accordion.Collapse>
                      //        </Card>
                      //       </Accordion>
                      //     )
                      // }
                    }):""}
                </div>
          </Col>
        </Row>

      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  cdata: state.Calender.cdata,
});


export default connect(mapStateToProps, {loadCalenderData})(Calendars)
