import React, { Component,useState } from 'react'
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import NumericLabel from 'react-pretty-numbers'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import {Row, Col} from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import { loadYData, searchData } from '../../store/actions/youtube';
import LinesEllipsis from 'react-lines-ellipsis'
import {withRouter} from 'react-router';
import ReactPlayer from 'react-player'
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import axios from 'axios';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import ShowMoreText from 'react-show-more-text';
import AvatarGroup from '@material-ui/lab/AvatarGroup';
import Transcript from './Transcript/Transcript';
import Comments from './Comments/Comments';
import { payment } from '../../store/actions/payment'

const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': false,
    'title': true
    };



let watch;


export class Watch extends Component {
    static propTypes = {
        payment: PropTypes.func.isRequired,
      };
    constructor(){
        super();
        this.state={
            posts:[],
            buttonName:"Transcript",
        }
    }
    executeOnClick(isExpanded) {
        console.log(isExpanded);
    }
    componentDidMount(){
        this.props.loadYData()
        let str = this.props.location.search
        watch  = str.split('=')[1]
        if(this.props.location.search){
            if(str.split('=')[1]){
                this.props.searchData(str.split('=')[1])  
            } else{
                this.props.history.push("/");
            } 
        }
        else if(!this.props.location.search){
            this.props.history.push("/");
        }

    }




    render() {
        const BASE_URL = `https://www.youtube.com/watch?v=${watch}`;
        {console.log(BASE_URL)}
        let data = ""
        console.log(this.props)

        function loadScript(src) {
            return new Promise((resolve) => {
                const script = document.createElement('script')
                script.src = src
                script.onload = () => {
                    resolve(true)
                }
                script.onerror = () => {
                    resolve(false)
                }
                document.body.appendChild(script)
            })
        }

        async function displayRazorpay() {
            const res = await loadScript('https://checkout.razorpay.com/v1/checkout.js')

            if (!res) {
                alert('Razorpay SDK failed to load. Are you online?')
                return
            }

            const data = await fetch('http://localhost:1337/razorpay', { method: 'POST' }).then((t) =>
                t.json(),
            )

            console.log(this.props)

            const options = {
                key: 'rzp_test_qIsGhLQiOFUXFJ',
                currency: data.currency,
                amount: data.amount.toString(),
                order_id: data.id,
                name: 'Donation',
                description: 'Thank you for nothing. Please give us some money',
                image: 'http://localhost:1337/logo.svg',
                handler: function (response) {
                    let val1  = response.razorpay_payment_id
                    let val2  = response.razorpay_order_id
                    let val3  = response.razorpay_signature
                    let val4  = data.currency
                    let val5  = data.id
                    let val6 =  data.amount
                    const val =  {
                        val1,
                        val2,
                        val3,
                        val4,
                        val5,
                        val6,
                    };
                    // this.props.payment(val);
                },
                prefill: {
                    name: '',
                    email: '',
                    phone_number: ''
                }
            }
            const paymentObject = new window.Razorpay(options)
            paymentObject.open()
        }



        if(this.state.buttonName==="Transcript"){
            data  = (<Transcript value={watch}/>)
        }
        else if(this.state.buttonName==="Comments"){
            data  = (<Comments value={watch}/>)
        }
        else if(this.state.buttonName==="Quiz"){
            data  = <h1>Quiz</h1>
        }
        else if(this.state.buttonName==="Tags"){
            data  = <h1>Tags</h1>
        }
        else if(this.state.buttonName==="Donate"){
            data  = <h1>Donate</h1>
        }



        return (
            <div>

            <div>
                <Row>
                    <Col xs={10}>
                        <div style={{ height: '700px' }}>
                        {this.props.data?this.props.data.map((data)=>{
                                if(data.videoId === watch){
                                    return(
                                        <ReactPlayer
                                        className='react-player'
                                        url={BASE_URL}
                                        width='100%'
                                        height='96%'
                                        playing
                                        controls
                                        showinfo
                                        />

                                    ) 
                                }
                            })
                            :
                            ""
                        }
                    </div>
                        <div>
                            <div >
                                {this.props.data?this.props.data.map((data)=>{
                                    if(data.videoId === watch){
                                        return(
                                            <div>
                                                <h4 style={{ fontWeight: '500' }}>
                                                    {data.title}
                                                </h4>
                                                <p>
                                                    <NumericLabel params={option}>{data.viewCount}</NumericLabel>  Views   •    Published  <Moment format="D MMM YYYY" withTitle>{data.publishedAt}</Moment>
                                                </p>
                                                <Divider light />
                                                <div style={{ margin: '10px'}}>
                                                    <div style={{ height: "100px", lineHeight: '100px', display: 'flex'  }}>
                                                        <div>
                                                            <Avatar src="http://punithargal.org/images/logo-alt-1.png" />
                                                        </div>
                                                        <div>
                                                            <h3 style={{fontWeight: '500', verticalAlign: 'middle', marginLeft: '20px'}}>
                                                                    Punithargal Saints
                                                            </h3>                                                        
                                                        </div>

                                                    </div>
                                                    <p>
                                                        <ShowMoreText
                                                            /* Default options */
                                                            lines={1}
                                                            more='Show more'
                                                            less='Show less'
                                                            anchorClass=''
                                                            onClick={this.executeOnClick}
                                                            expanded={false}
                                                            width={1000}
                                                        >
                                                            {data.description}
                                                        </ShowMoreText>

                                                    </p>
                                                </div>
                                                <Divider light />
                                                <div style={{ width: '100%', height: '80px' }}>
                                                   <Row style={{ height: '100%' }}>
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Transcript'
                                                            })}>
                                                                Transcript
                                                            </Button>
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Comments'
                                                            })}>
                                                                Comments
                                                            </Button>                                                      
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Quiz'
                                                            })}>
                                                                Quiz
                                                            </Button>                                                      
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Tags'
                                                            })}>
                                                                Tags
                                                            </Button>                                                      
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button  style={{ width: '100%', height: '100%'  }} onClick={displayRazorpay}>
                                                                Donate
                                                            </Button>                                                      
                                                       </Col>
                                                   </Row>
                                                </div>
                                            </div>

                                        )
                                    }
                                }):
                                ""
                                }
                                <div style={{ margin: '30px' }}>
                                    {data}
                                </div>
                            </div>

                        </div>
                    </Col>
                    <Col xs={2}>
                    </Col>
                </Row>
            </div>
            </div>
        )
    }
}

