import React, { Component } from 'react'
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import NumericLabel from 'react-pretty-numbers'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import {Row, Col} from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import { loadYData, searchData } from '../../store/actions/youtube';
import LinesEllipsis from 'react-lines-ellipsis'
import {withRouter} from 'react-router';
import ReactPlayer from 'react-player'
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import axios from 'axios';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import ShowMoreText from 'react-show-more-text';
import AvatarGroup from '@material-ui/lab/AvatarGroup';

const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': false,
    'title': true
    };

let watch;


export class Watch extends Component {
    constructor(){
        super();
        this.state={
            posts:[],
            buttonName:"Transcript",
        }
    }
    executeOnClick(isExpanded) {
        console.log(isExpanded);
    }
    componentDidMount(){
        this.props.loadYData()
        let str = this.props.location.search
        watch  = str.split('=')[1]
        if(this.props.location.search){
            if(str.split('=')[1]){
                this.props.searchData(str.split('=')[1])  
            } else{
                this.props.history.push("/");
            } 
        }
        else if(!this.props.location.search){
            this.props.history.push("/");
        }

    }

    render() {
        const BASE_URL = `https://www.youtube.com/watch?v=${watch}`;
        {console.log(BASE_URL)}
        let data = ""
        console.log(this.state.buttonName)
        if(this.state.buttonName==="Transcript"){
            data  = (<h1>Transcript</h1>)
        }
        else if(this.state.buttonName==="Comments"){
            data  = <h1>Comments</h1>
        }
        else if(this.state.buttonName==="Quiz"){
            data  = <h1>Quiz</h1>
        }
        else if(this.state.buttonName==="Tags"){
            data  = <h1>Tags</h1>
        }
        else if(this.state.buttonName==="Donate"){
            data  = <h1>Donate</h1>
        }
        return (
            <div style={{ height: '100%', width: '100%' }}>
                <Row>
                    <Col xs={10}>
                        <div style={{height:'220%', }}>
                                {this.props.data?this.props.data.map((data)=>{
                                    if(data.videoId === watch){
                                          return(
                                            <ReactPlayer
                                            className='react-player'
                                            url={BASE_URL}
                                            width='100%'
                                            height='70%'
                                            playing
                                            controls
                                            showinfo
                                            />
                                          ) 
                                    }
                                })
                                :
                                ""
                            }

                            <div style={{ marginTop: '30px' }}>
                                {this.props.data?this.props.data.map((data)=>{
                                    if(data.videoId === watch){
                                        return(
                                            <div>
                                                <h4 style={{ fontWeight: '500' }}>
                                                    {data.title}
                                                </h4>
                                                <p>
                                                    <NumericLabel params={option}>{data.viewCount}</NumericLabel>  Views   •    Published  <Moment format="D MMM YYYY" withTitle>{data.publishedAt}</Moment>
                                                </p>
                                                <Divider light />
                                                <div style={{ margin: '10px'}}>
                                                    <div style={{ height: "100px", lineHeight: '100px', display: 'flex'  }}>
                                                        <div>
                                                            <Avatar src="http://punithargal.org/images/logo-alt-1.png" />
                                                        </div>
                                                        <div>
                                                            <h3 style={{fontWeight: '500', verticalAlign: 'middle', marginLeft: '20px'}}>
                                                                    Punithargal Saints
                                                            </h3>                                                        
                                                        </div>

                                                    </div>
                                                    <p>
                                                        <ShowMoreText
                                                            /* Default options */
                                                            lines={1}
                                                            more='Show more'
                                                            less='Show less'
                                                            anchorClass=''
                                                            onClick={this.executeOnClick}
                                                            expanded={false}
                                                            width={1000}
                                                        >
                                                            {data.description}
                                                        </ShowMoreText>

                                                    </p>
                                                </div>
                                                <Divider light />
                                                <div style={{ width: '100%', height: '80px' }}>
                                                   <Row style={{ height: '100%' }}>
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Transcript'
                                                            })}>
                                                                Transcript
                                                            </Button>
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Comments'
                                                            })}>
                                                                Comments
                                                            </Button>                                                      
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Quiz'
                                                            })}>
                                                                Quiz
                                                            </Button>                                                      
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Tags'
                                                            })}>
                                                                Tags
                                                            </Button>                                                      
                                                       </Col>
                                                       <Divider orientation="vertical" flexItem />
                                                       <Col>
                                                            <Button style={{ width: '100%', height: '100%' }} onClick={()=>this.setState({
                                                                buttonName: 'Donate'
                                                            })}>
                                                                Donate
                                                            </Button>                                                      
                                                       </Col>
                                                   </Row>
                                                </div>
                                            </div>

                                        )
                                    }
                                }):
                                ""
                                }
                                <div style={{ margin: '30px' }}>
                                    {data}
                                </div>
                            </div>

                        </div>
                    </Col>
                    <Col xs={2}>
                    </Col>
                </Row>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    searchdata: state.youtube,
    data: state.youtube.ydata
  });

export default connect(mapStateToProps,{loadYData, searchData})(withRouter(Watch));
