import React, { Component } from 'react'
import { Form, FormControl,Button } from 'react-bootstrap';
import SearchIcon from '@material-ui/icons/Search';
import { connect } from 'react-redux';
import { searchData } from '../../store/actions/youtube';
import { Redirect } from 'react-router-dom';
import { useHistory } from "react-router-dom";



export  class Search extends Component {
  state = {
    query: '',
  };


  onSubmit = (e) => {
    <Redirect to="results?search_query=hello" />
  };


  onChange = (e) => this.setState({ [e.target.name]: e.target.value });


  render() {
    let history = useHistory()
    const { query } = this.state;
    return (
      <div style={{ width: '150%' }}>
        <Form inline >
            <FormControl type="text" 
            placeholder="Search" 
            className="mr-sm-2" 
            name="query"
            id="query"
            style={{ width: '450px',  marginRight: '0px !important', marginLeft: '200px' }} 
            onChange={this.onChange}
            value={query}
            />
            <Button onClick={this.onSubmit}><SearchIcon /></Button>
         </Form>
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  data: state.youtube
});

export default connect(mapStateToProps,{searchData})(Search);
