import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import HomeIcon from '@material-ui/icons/Home';
import AppsIcon from '@material-ui/icons/Apps';
import EventIcon from '@material-ui/icons/Event';
import VideoLibraryIcon from '@material-ui/icons/VideoLibrary';
import WhatshotIcon from '@material-ui/icons/Whatshot';
import MusicVideoIcon from '@material-ui/icons/MusicVideo';
import PollIcon from '@material-ui/icons/Poll';
import ViewQuiltIcon from '@material-ui/icons/ViewQuilt';
import Tooltip from '@material-ui/core/Tooltip';
import Link from '@material-ui/core/Link';

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
});

export default function MiniDrawer() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  if(store.auth.isAuthenticated){
    cusCon = (
      <div style={{ width: '-webkit-fill-available' }}>
        <Tooltip title="Accounts" placement="down">
        <NavDropdown title={<AccountCircle />} style={{ textDecorationLine: "transparent", top: '3px' ,float: 'right' }} id="nav-dropdown">
            <Card style={{ background: '#ffffff',  boxShadow: 'none', width: '280px' }}>
              <CardContent style={{  }}>
                <Row>
                  <Col xs={3}>
                    <Avatar style={{ height: '40px',  width:  '40px'  }}>{store.auth.user?store.auth.user.first_name.slice(0,1).toUpperCase():""}</Avatar>
                  </Col>
                  <Col xs={9}>
                    <Typography style={{  alignContent: 'right', justifyContent: 'right', display: 'flex'  }} variant="h6" noWrap>
                        {store.auth.user?store.auth.user.first_name+store.auth.user.last_name:"UserName"}
                    </Typography> 
                    <Typography style={{ color: '#333333',  fontSize: '11px' , alignContent: 'right', justifyContent: 'right', display: 'flex'  }} variant="h6" noWrap>
                        {store.auth.user?store.auth.user.email:"Email"}
                    </Typography> 
                  </Col>
                </Row>
              </CardContent>
              <Divider />
              <StyledMenuItem  onClick={handleNav}>
                <ListItemIcon>
                  <SettingsIcon fontSize="small" />
                </ListItemIcon>
                <ListItemText primary="Account Settings" />
              </StyledMenuItem>
            </Card>
        </NavDropdown>
        </Tooltip>
        <Tooltip title="Notification" placement="down">
        <NavDropdown title={<NotificationsIcon />} id="nav-dropdown" style={{ textDecorationLine: "transparent", top: '3px' , boxShadow: '30px solid  #eeee',float: 'right' }}>

        </NavDropdown>
        </Tooltip>
     </div>
    )
  }
  else{
    cusCon = (
      <Button  onClick ={loginNav}>Signin</Button>
    )
  }

  const list = (anchor) => (
    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === 'top' || anchor === 'bottom',
      })}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
              <List>
        <Tooltip title="Home" placement="right">
          <Link  href='/'>
          <ListItem button>
                <ListItemIcon><HomeIcon /></ListItemIcon>
                <ListItemText >Home</ListItemText>
          </ListItem>
          </Link>
        </Tooltip>
        <Tooltip title="Blog" placement="right">
          <Link  href='/blog'>
          <ListItem button>
                <ListItemIcon><AppsIcon /></ListItemIcon>
                <ListItemText >Blog</ListItemText>
          </ListItem>
          </Link>
        </Tooltip>
        <Tooltip title="Calender" placement="right">
          <Link  href='/calender'>
          <ListItem button>
                <ListItemIcon><EventIcon /></ListItemIcon>
                <ListItemText >Calender</ListItemText>
          </ListItem>
          </Link>
        </Tooltip>
        </List>
        <Divider />
        <List>
        <Tooltip title="Library" placement="right">
          <ListItem button >
              <ListItemIcon><VideoLibraryIcon /></ListItemIcon>
              <ListItemText >Library</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Trending" placement="right">
          <Link  href='/trending'>
          <ListItem button>
                <ListItemIcon><WhatshotIcon /></ListItemIcon>
                <ListItemText >Trending Videos</ListItemText>
          </ListItem>
          </Link>
        </Tooltip>
        <Tooltip title="Latest Uploads" placement="right">
          <Link  href='/recent'>
          <ListItem button>
                <ListItemIcon><MusicVideoIcon /></ListItemIcon>
                <ListItemText >Recent Videos</ListItemText>
          </ListItem>
          </Link>
        </Tooltip>
        <Tooltip title="Popular Saints" placement="right">
          <ListItem button >
              <ListItemIcon><PollIcon /></ListItemIcon>
              <ListItemText >Popular Saints</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Mother Mary" placement="right">
          <ListItem button >
              <ListItemIcon><ViewQuiltIcon /></ListItemIcon>
              <ListItemText >Mother Mary</ListItemText>
          </ListItem>
        </Tooltip>
        </List>
    </div>
  );

  return (
    <div>
      {['left', 'right', 'top', 'bottom'].map((anchor) => (
        <React.Fragment key={anchor}>
          <Button onClick={toggleDrawer(anchor, true)}>{anchor}</Button>
          <SwipeableDrawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
            onOpen={toggleDrawer(anchor, true)}
          >
            {list(anchor)}
          </SwipeableDrawer>
        </React.Fragment>
      ))}
    </div>
  );
}
