import React ,{Suspense} from 'react';
import clsx from 'clsx';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import Drawer from '@material-ui/core/Drawer';

import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import HomeIcon from '@material-ui/icons/Home';
import VideoLibraryIcon from '@material-ui/icons/VideoLibrary';
import Tooltip from '@material-ui/core/Tooltip';
import { createStyles, makeStyles, useTheme, Theme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import { LinearProgress } from '@material-ui/core';
import AccountCircle from '@material-ui/icons/AccountCircle';
import NotificationsIcon from '@material-ui/icons/Notifications';
import {isMobile} from 'react-device-detect';
import './AppBar.css'
import AppsIcon from '@material-ui/icons/Apps';
import EventIcon from '@material-ui/icons/Event';
import WhatshotIcon from '@material-ui/icons/Whatshot';
import MusicVideoIcon from '@material-ui/icons/MusicVideo';
import PollIcon from '@material-ui/icons/Poll';
import ViewQuiltIcon from '@material-ui/icons/ViewQuilt';
import { useSelector } from 'react-redux'
import { NavDropdown } from 'react-bootstrap'
import { useHistory } from "react-router-dom";
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import { Row, Col } from 'react-bootstrap';
import MenuItem from '@material-ui/core/MenuItem';
import { withStyles } from '@material-ui/core/styles';
import SettingsIcon from '@material-ui/icons/Settings';
import { renderRoutes } from 'react-router-config';
import Link from '@material-ui/core/Link';
import Search from './Search'


  const drawerWidth = 240;
  const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
    },
    appBar: {
      zIndex: theme.zIndex.drawer + 1,
      transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
    },
    appBarShift: {
      marginLeft: drawerWidth,
      width: `calc(100% - ${drawerWidth}px)`,
      transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
    },
    menuButton: {
      marginRight: 36,
    },
    hide: {
      display: 'none',
    },
    drawer: {
      width: drawerWidth,
      flexShrink: 0,
      whiteSpace: 'nowrap',
    },
    drawerOpen: {
      width: drawerWidth,
      transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
    },
    drawerClose: {
      transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      overflowX: 'hidden',
      width: theme.spacing(7) + 1,
      [theme.breakpoints.up('sm')]: {
        width: theme.spacing(9) + 1,
      },
    },
    toolbar: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'flex-end',
      padding: theme.spacing(0, 1),
      // necessary for content to be below app bar
      ...theme.mixins.toolbar,
    },
    content: {
      flexGrow: 1,
      paddingTop: theme.spacing(0),
      paddingLeft: theme.spacing(15),
    },
  }));


export default function MiniDrawer(props) {
    const classes = useStyles();
    const theme = useTheme();
    const [open, setOpen] = React.useState(false);
      function loginNav() {
      history.push("/account/login");
    }
    function handleNav(){
      console.log('clicked')
      history.push("/settings");
    }
    let history = useHistory();

    const store = useSelector(state => state)
    const { route } = props;


    const StyledMenuItem = withStyles((theme) => ({
      root: {
        '&:focus': {
          backgroundColor: theme.palette.primary.main,
          '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
            color: theme.palette.common.white,
          },
        },
      },
    }))(MenuItem);
    let cusCon;

    if(store.auth.isAuthenticated){
      cusCon = (
        <div style={{ width: '-webkit-fill-available' }}>
          <Tooltip title="Accounts" placement="down">
          <NavDropdown title={<AccountCircle />} style={{ textDecorationLine: "transparent", top: '3px' ,float: 'right' }} id="nav-dropdown">
              <Card style={{ background: '#ffffff',  boxShadow: 'none', width: '280px' }}>
                <CardContent style={{  }}>
                  <Row>
                    <Col xs={3}>
                      <Avatar style={{ height: '40px',  width:  '40px'  }}>{store.auth.user?store.auth.user.first_name.slice(0,1).toUpperCase():""}</Avatar>
                    </Col>
                    <Col xs={9}>
                      <Typography style={{  alignContent: 'right', justifyContent: 'right', display: 'flex'  }} variant="h6" noWrap>
                          {store.auth.user?store.auth.user.first_name+store.auth.user.last_name:"UserName"}
                      </Typography> 
                      <Typography style={{ color: '#333333',  fontSize: '11px' , alignContent: 'right', justifyContent: 'right', display: 'flex'  }} variant="h6" noWrap>
                          {store.auth.user?store.auth.user.email:"Email"}
                      </Typography> 
                    </Col>
                  </Row>
                </CardContent>
                <Divider />
                <StyledMenuItem  onClick={handleNav}>
                  <ListItemIcon>
                    <SettingsIcon fontSize="small" />
                  </ListItemIcon>
                  <ListItemText primary="Account Settings" />
                </StyledMenuItem>
              </Card>
          </NavDropdown>
          </Tooltip>
          <Tooltip title="Notification" placement="down">
          <NavDropdown title={<NotificationsIcon />} id="nav-dropdown" style={{ textDecorationLine: "transparent", top: '3px' , boxShadow: '30px solid  #eeee',float: 'right' }}>

          </NavDropdown>
          </Tooltip>
      </div>
      )
    }
    else{
      cusCon = (
        <Button  onClick ={loginNav}>Signin</Button>
      )
    }

  const list = (anchor) => (
      <div
        className={clsx(classes.list, {
          [classes.fullList]: anchor === 'top' || anchor === 'bottom',
        })}
        role="presentation"
        onClick={toggleDrawer(anchor, false)}
        onKeyDown={toggleDrawer(anchor, false)}
      >
          <List>
            <Tooltip title="Home" placement="right">
              <Link  href='/'>
              <ListItem button>
                    <ListItemIcon><HomeIcon /></ListItemIcon>
                    <ListItemText >Home</ListItemText>
              </ListItem>
              </Link>
            </Tooltip>
            <Tooltip title="Blog" placement="right">
              <Link  href='/blog'>
              <ListItem button>
                    <ListItemIcon><AppsIcon /></ListItemIcon>
                    <ListItemText >Blog</ListItemText>
              </ListItem>
              </Link>
            </Tooltip>
            <Tooltip title="Calender" placement="right">
              <Link  href='/calender'>
              <ListItem button>
                    <ListItemIcon><EventIcon /></ListItemIcon>
                    <ListItemText >Calender</ListItemText>
              </ListItem>
              </Link>
            </Tooltip>
            </List>
            <Divider />
            <List>
            <Tooltip title="Library" placement="right">
              <ListItem button >
                  <ListItemIcon><VideoLibraryIcon /></ListItemIcon>
                  <ListItemText >Library</ListItemText>
              </ListItem>
            </Tooltip>
            <Tooltip title="Trending" placement="right">
              <Link  href='/trending'>
              <ListItem button>
                    <ListItemIcon><WhatshotIcon /></ListItemIcon>
                    <ListItemText >Trending Videos</ListItemText>
              </ListItem>
              </Link>
            </Tooltip>
            <Tooltip title="Latest Uploads" placement="right">
              <Link  href='/recent'>
              <ListItem button>
                    <ListItemIcon><MusicVideoIcon /></ListItemIcon>
                    <ListItemText >Recent Videos</ListItemText>
              </ListItem>
              </Link>
            </Tooltip>
            <Tooltip title="Popular Saints" placement="right">
              <ListItem button >
                  <ListItemIcon><PollIcon /></ListItemIcon>
                  <ListItemText >Popular Saints</ListItemText>
              </ListItem>
            </Tooltip>
            <Tooltip title="Mother Mary" placement="right">
              <ListItem button >
                  <ListItemIcon><ViewQuiltIcon /></ListItemIcon>
                  <ListItemText >Mother Mary</ListItemText>
              </ListItem>
            </Tooltip>
            </List>
        </div>
  );
  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };
  const toggleDrawer = (anchor, open) => (event) => {
    if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };
  return (
    <div>
      {['left'].map((anchor) => (
        <React.Fragment key={anchor}>
            <CssBaseline />
            <AppBar
                position="fixed"
                elevation={0}>
                  <Toolbar style={{ backgroundColor: '#ffffff'}} >
                    <IconButton
                      color="#606060"
                      aria-label="open drawer"
                      anchor='left'
                      onClick={state['left']}
                      onClose={toggleDrawer('left', false)}
                      onOpen={toggleDrawer('left', true)}
                      edge="start"
                      onClick={toggleDrawer('left', true)}>

                      <MenuIcon />

                    </IconButton>
                    <img src="http://punithargal.org/images/logo-alt-1.png" style={{ height: '30px', width: '30px' }}/>
                      <Search />
                  {cusCon}
                  </Toolbar>

          </AppBar>
          <Button onClick={toggleDrawer(anchor, true)}>{anchor}</Button>
          <Drawer
              variant="permanent"
              className={clsx(classes.drawer, {
                [classes.drawerOpen]: open,
                [classes.drawerClose]: !open,
              })}
              classes={{
                paper: clsx({
                  [classes.drawerOpen]: open,
                  [classes.drawerClose]: !open,
                }),
              }}
            >
              <div className={classes.toolbar}>
                <IconButton onClick={handleDrawerClose}>
                  {theme.direction === 'rtl' ? <ChevronRightIcon /> : <ChevronLeftIcon />}
                </IconButton>
              </div>
              <Divider />
              <List>
                {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
                  <ListItem button key={text}>
                    <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                    <ListItemText primary={text} />
                  </ListItem>
                ))}
              </List>
              <Divider />
              <List>
                {['All mail', 'Trash', 'Spam'].map((text, index) => (
                  <ListItem button key={text}>
                    <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                    <ListItemText primary={text} />
                  </ListItem>
                ))}
              </List>
            </Drawer>
          <main className={classes.content}  style={{ backgroundColor: '#ffffff', minHeight: '-webkit-fill-available' }} >
            <div className={classes.toolbar} />
                <Suspense fallback={<LinearProgress />}>
                  {renderRoutes(route.routes)}
                </Suspense> 
          </main> 
        </React.Fragment>

      ))}
    </div>
  );
}
