import React, { useState, useEffect, Component } from 'react';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import NumericLabel from 'react-pretty-numbers'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import {Row, Col} from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import { searchData } from '../../store/actions/youtube';
import LinesEllipsis from 'react-lines-ellipsis'
import {withRouter} from 'react-router';
import CircularProgress from '@material-ui/core/CircularProgress';
import InfiniteScroll from 'react-infinite-scroll-component';


const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };


    const ListData = (props) => () => {
        const [listItems, setListItems] = useState(Array.from(Array(30).keys(), n => n + 1));
        const [isFetching, setIsFetching] = useState(false);

        useEffect(() => {
          window.addEventListener('scroll', handleScroll);
          return () => window.removeEventListener('scroll', handleScroll);
        }, []);

        useEffect(() => {
          if (!isFetching) return;
          fetchMoreListItems();
        }, [isFetching]);

        function handleScroll() {
          if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight || isFetching) return;
          setIsFetching(true);
        }

        function fetchMoreListItems() {
          setTimeout(() => {
            setListItems(prevState => ([...prevState, ...Array.from(Array(20).keys(), n => n + prevState.length + 1)]));
            setIsFetching(false);
          }, 2000);
        }

        return (
          <>
            <ul className="list-group mb-2">
              {listItems.map(listItem => <li className="list-group-item">List Item {listItem}</li>)}
            </ul>
            {isFetching && 'Fetching more list items...'}
          </>
        );
      };


export class List extends Component {

    constructor(props){
        super(props);
        this.state={
            isFetching:false,
            setIsFetching: false,
            listItems:Array.from(Array(30).keys(), n => n + 1),
            setListItems:Array.from(Array(30).keys(), n => n + 1),
        }
    }
    componentDidMount(){
        let str = this.props.location.search
        if(this.props.location.search){
            if(str.split('=')[1]){
                console.log("hey louji")
                this.props.searchData(str.split('=')[1])  
            } else{
                this.props.history.push("/");
            } 
        }
        else if(!this.props.location.search){
            this.props.history.push("/");
        }
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }

    render() {
        return (
            <div className="container">
                <ListData value={this.props.searchdata.ysearchdata ? this.props.searchdata.ysearchdata:""}/>
                     <h1>
                         No videos 
                     </h1>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    searchdata: state.youtube
  });

export default connect(mapStateToProps,{searchData})(withRouter(List));
