import React, { Component } from 'react';
import {loadYData} from '../../store/actions/youtube'; 
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import store from '../../store/configureStore';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/styles';
import {Row, Col} from 'react-bootstrap'
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import NumericLabel from 'react-pretty-numbers'
import Recent from  './Recent/Recent'
import Error from '../../Error'
import { SnackbarProvider, useSnackbar } from 'notistack';

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });


  const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };


export class RecentVideos extends Component {
    static propTypes = {
        loadYData: PropTypes.func.isRequired,
        isLoading: PropTypes.bool,
        ydata:  PropTypes.array,
        loading: PropTypes.bool,
      };

      componentDidMount(){
        this.props.loadYData()
      }

    render() {
        const le  = this.props.ydata
        const l2 = le?le.length:""
        const loading = this.props;
        const isOnline = window.navigator.onLine;
        console.log(this.props.isLoading)
        if(this.props.isLoading){
          return(
            <Skeleton />
          )
        }
        return (
          <div>
            <div>
              <h1>
                Trending Videos
              </h1>
              <Row>
                {(this.props.isLoading ? Array.from(new Array(3)) : (this.props.ydata?this.props.ydata:Array.from(new Array(3))))}
              </Row>
            </div>
            <div>
              <Recent />
            </div>
                <SnackbarProvider maxSnack={3}>
                    <Error value={this.props.error}/>
                    {console.log(isOnline)}
                    {isOnline
                    ?""
                    :<Error value={"Check  your Internet Connection"}/>
                    }
                </SnackbarProvider>
          </div>
        )
    }
}

const mapStateToProps = (state) => ({
    isLoading: state.youtube.isLoading,
    ydata: state.youtube.ydata,
    error: state.auth.error,
  });

export default connect(mapStateToProps, {loadYData})(RecentVideos)
