import React, { Component } from 'react';
import {loadYData} from '../../../store/actions/youtube'; 
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/styles';
import {Row, Col} from 'react-bootstrap'
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import NumericLabel from 'react-pretty-numbers'
import {withRouter} from 'react-router';


const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });


  const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };


export class Recent extends Component {
    static propTypes = {
        loadYData: PropTypes.func.isRequired,
        isLoading: PropTypes.bool,
        ydata:  PropTypes.array,
        loading: PropTypes.bool,
      };

      componentDidMount(){
        this.props.loadYData()
        console.log(this.props)
      }


    render() {
        console.log(this.props.ydata)
        return (
          <div>
            <div>
            {this.props.isLoading?
              ""
            :
              <Typography style={{ fontWeight: '600' }} gutterBottom variant="h5">
                Latest
            </Typography>
            }
              <Row>
                    {this.props.ydata?this.props.ydata.sort((a,b)=>{
                      return (a.publishedAt > b.publishedAt) ? 1 : -1 
                    })
                    .reverse()     
                    .slice(0,8)               
                    .map((item)=>(
                    <Col xs={3}>
                        <div style={{ maxWidth: '325px' , cursor: 'pointer'}}  elevation={0} onClick={e => this.props.history.push(`/watch?v=${item.videoId}`)}>
                            <div>
                                <CardMedia
                                component="img"
                                alt="Contemplative Reptile"
                                height="140"
                                image={item.thumbnailsHight}
                                title="Contemplative Reptile"
                                />
                                <CardContent>
                                <Typography gutterBottom variant="h6" component="h5">
                                    {item.title || <Skeleton />}
                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="p">
                                    {item.channelTitle || <Skeleton />}
                                </Typography>
                                <Row>
                                  <Col>
                                    <Row xs={3}>
                                      <Col xs={1}>
                                          <Typography variant="body2" color="textSecondary" component="p">
                                              <NumericLabel params={option}>{item.viewCount}</NumericLabel> 
                                          </Typography>
                                      </Col>
                                      <Col xs={2}>
                                        <Typography variant="body2" color="textSecondary" component="p">
                                              views
                                          </Typography>
                                      </Col>
                                    </Row>

                                  </Col>
                                  <Col>
                                  <Typography variant="body2" color="textSecondary" component="p">
                                        <Moment fromNow>{item.publishedAt}</Moment>
                                  </Typography>
                                  </Col>
                                </Row>
                                </CardContent>
                            </div>
                        </div>
                    </Col>
                    )):
                      ""
                  } 
                </Row>
            </div>
          </div>
        )
    }
}

const mapStateToProps = (state) => ({
    isLoading: state.youtube.isLoading,
    ydata: state.youtube.ydata,
  });

export default connect(mapStateToProps, {loadYData})(withRouter(Recent))
