import React, { Component } from 'react'
import axios from 'axios'
import { Button, Comment, Form, Header } from 'semantic-ui-react'

export default class Comments extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: []
    }
  }

    componentDidMount(){
        axios.get(`https://www.googleapis.com/youtube/v3/commentThreads?part=snippet%2Creplies&maxResults=1000&videoId=${this.props.value}&key=AIzaSyBqXFMg2KDNkXlhPU27w1Zcphm00su7cI0`)
        .then((response) => {
          console.log(typeof(response.data.items))
            this.setState(()=>{
              return{data: response.data.items}
            })
        })
        .catch((error) => {
            console.log(error);
        });
    }
    render() {
      console.log("this.state.data")
      console.log(this.state.data)
        return (
            <div>
                <Comment.Group>
                <Header as='h3' dividing>
                  {console.log(this.state.data)}Comments
                </Header>
                <Comment>
                  <Comment.Avatar src='https://react.semantic-ui.com/images/avatar/small/matt.jpg' />
                  <Comment.Content>
                    <Comment.Author as='a'>Matt</Comment.Author>
                    <Comment.Metadata>
                      <div>Today at 5:42PM</div>
                    </Comment.Metadata>
                    <Comment.Text>How artistic!</Comment.Text>
                    <Comment.Actions>
                      <Comment.Action>Reply</Comment.Action>
                    </Comment.Actions>
                  </Comment.Content>
                </Comment>
                </Comment.Group>
            </div>
        )
    }
}
