import PropTypes from 'prop-types';
var align = PropTypes.oneOf(['start', 'end', 'flex-start', 'flex-end', 'center', 'stretch', 'baseline', 'first-baseline', 'last-baseline']);
var content = PropTypes.oneOf(['left', 'right', 'flex-start', 'flex-end', 'center', 'baseline', 'first-baseline', 'last-baseline', 'space-between', 'space-around', 'space-evenly']);
export var propTypes = {
  as: PropTypes.elementType.isRequired,
  direction: PropTypes.oneOf(['row', 'column']).isRequired,
  pad: PropTypes.oneOfType([PropTypes.bool, PropTypes.number]),
  wrap: PropTypes.bool,
  grow: PropTypes.bool,
  inline: PropTypes.bool,
  flex: PropTypes.any,
  align: align.isRequired,
  alignSelf: align,
  alignContent: content,
  justify: content.isRequired
};
export var defaultProps = {
  as: 'div',
  direction: 'row',
  align: 'stretch',
  justify: 'flex-start'
};
