import axios from 'axios';
import {
    PAYMENTLOADING,
    PAYMENTDONE
} from './types';


// REGISTER USER
export const payment = ({ razorpay_payment_id,razorpay_order_id, razorpay_signature, currency,amount,order_id }) => (dispatch) => {

  const body = JSON.stringify({ razorpay_payment_id,razorpay_order_id, razorpay_signature, currency,amount,order_id });

  axios
    .post('http://localhost:8000/youtube/api/payment', body)
    .then((res) => {
      dispatch({
        type: PAYMENTDONE,
        payload: res.data,
      });
    })
    .catch((err) => {
        console.log(err)
    });
};
