import React, { Component } from 'react'
import Toolbar from '@material-ui/core/Toolbar';
import Link from '@material-ui/core/Link';
import { Divider, Grid } from '@material-ui/core';
import {Container,Image,  Row, Col}  from 'react-bootstrap'
import Typography from '@material-ui/core/Typography';
import {loadBlog} from '../../store/actions/blog';
import { connect } from 'react-redux';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import NumericLabel from 'react-pretty-numbers'
import CardMedia from '@material-ui/core/CardMedia';


const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };
export  class Blog extends Component {

    componentDidMount(){
        this.props.loadBlog()
        console.log(this.props.bdata)
      }
    render() {
        return (
           <Grid container>
               {this.props.bdata?this.props.bdata.slice(0,6).map((data)=>{
                  return(
                    <Grid xs={3} style={{ flexBasis: '24%', cursor: 'pointer', transform: this.props.DrawerStateOpen?"scale(0.90)":"" }} nopadding>
                    <Link>
                     <Box  height={260} width={360} marginRight={0.5} my={5}>
                             {data ? (
                                 <CardMedia
                                 style={{ width: "inherit", height: 200 , objectFit: 'cover' }}                                   
                                 image={data.thumbnailsHight}
                                 title={data.title}>
                                 </CardMedia>
                             ) : (
                                 <Skeleton variant="rect" width="inherit" height={180} animation={false} style={{ backgroundColor: 'rgba(0, 0, 0, 0.14)' }}/>
                             )}
                             {data ? (
                             <Box pr={2} >
                                 <Typography style={{ fontWeight: '500', display: '-webkit-box', WebkitLineClamp: '2', WebkitBoxOrient: 'vertical', overflow: 'hidden',textOverflow:  'hidden' }} gutterBottom variant="h6">
                                 {data.title}
                                 </Typography>
                             </Box>
                             ) : (
                             <div>
                             <Box pt={0.5}>
                                 <Skeleton variant="rect" height={20} width="90%"  animation={false} style={{ backgroundColor: 'rgba(0, 0, 0, 0.14)', marginTop: '10px' }}/>
                             </Box>
                             </div>
                             )}
                         </Box>
                    </Link>
                 </Grid>
                  )
               }):""}
           </Grid>
        )
    }
}

const mapStateToProps = (state) => ({
    bdata: state.blog.bdata
  });



export default connect(mapStateToProps, {loadBlog})(Blog)
