import React, { Component } from 'react';
import {loadYData} from '../../../store/actions/youtube'; 
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/styles';
import {Row, Col} from 'react-bootstrap'
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import NumericLabel from 'react-pretty-numbers'
import LinesEllipsis from 'react-lines-ellipsis'

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });


  const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };


export class Recents extends Component {
    static propTypes = {
        loadYData: PropTypes.func.isRequired,
        ydata:  PropTypes.array,
      };

    render() {
        console.log(this.props.ydata)
        return (
          <div>
            <div>
              <h1>
                Trending Videos
              </h1>
              {this.props.ydata?this.props.ydata
                    .sort((a,b)=>{
                        return (a.publishedAt > b.publishedAt) ? 1 : -1 
                    })
                    .reverse()     
                    .slice(0,25)  
                    .map((data)=>(
                        <Row xs={12} style={{ cursor: 'pointer' }} onClick={e => this.props.history.push(`/watch?v=${data.videoId}`)}>
                            <Col xs={3}>
                                <div style={{ width: '260px', height: '200px' }}>
                                    <CardMedia
                                    component="img"
                                    alt="Contemplative Reptile"
                                    height="140"
                                    image={data.thumbnailsHight}
                                    title="Contemplative Reptile"
                                    />
                                </div>
                            </Col>
                            <Col xs={9}>
                                <CardContent  style={{ padding: '0' }}>
                                    <Typography gutterBottom variant="h6" component="h5">
                                        {data.title}
                                    </Typography>
                                    <LinesEllipsis
                                    text={data.description}
                                    maxLine='4'
                                    ellipsis='......'
                                    trimRight
                                    basedOn='letters'
                                    />
                                    <Row>
                                  <Col style={{ marginTop: '20px' }}>
                                    <Row xs={3}>
                                      <Col xs={1}>
                                          <Typography variant="body2" color="textSecondary" component="p">
                                              <NumericLabel params={option}>{data.viewCount}</NumericLabel> 
                                          </Typography>
                                      </Col>
                                      <Col xs={2}>
                                        <Typography variant="body2" color="textSecondary" component="p">
                                              views
                                          </Typography>
                                      </Col>
                                    </Row>

                                  </Col>
                                  <Col  style={{ marginTop: '20px' }}>
                                  <Typography variant="body2" color="textSecondary" component="p">
                                       Published  <Moment fromNow>{data.publishedAt}</Moment>
                                  </Typography>
                                  </Col>
                                </Row>
                                </CardContent>                   
                            </Col>
                        </Row>
                    )):
                     "No videos found"
                     }
            </div>
          </div>
        )
    }
}

const mapStateToProps = (state) => ({
    isLoading: state.youtube.isLoading,
    ydata: state.youtube.ydata,
  });

export default connect(mapStateToProps, {loadYData})(Recents)
