import React, { Component } from 'react';
import {loadYData} from '../../store/actions/youtube'; 
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import store from '../../store/configureStore';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/styles';
import {Row, Col} from 'react-bootstrap'
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import NumericLabel from 'react-pretty-numbers'
import Recent from  './Recent/Recent'
import Error from '../../Error'
import { SnackbarProvider, useSnackbar } from 'notistack';

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });


  const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };


export class RecentVideos extends Component {
    static propTypes = {
        loadYData: PropTypes.func.isRequired,
        isLoading: PropTypes.bool,
        ydata:  PropTypes.array,
        loading: PropTypes.bool,
      };

      componentDidMount(){
        this.props.loadYData()
      }

    render() {
        const le  = this.props.ydata
        const l2 = le?le.length:""
        const loading = this.props;
        const isOnline = window.navigator.onLine;

        return (
          <div>
            <div>
              <h1>
                Trending Videos
              </h1>
              <Row>
                    {(this.props.isLoading ? (Array.from(new Array(8))) : (this.props.ydata?this.props.ydata:"")).sort((a,b)=>{
                      return (a.viewCount > b.viewCount) ? 1 : -1 
                    }).reverse()
                    .slice(0,8)
                    .map((data)=>(
                    <Col xs={3} key={data?data.id:""}>
                      {console.log(this.props.isLoading)}
                        <div style={{ maxWidth: '325px', cursor: 'pointer'}}  elevation={0} onClick={e => this.props.history.push(`/watch?v=${data.videoId}`)}>
                            <div>
                              {data?
                                  <CardMedia
                                  component="img"
                                  alt="Contemplative Reptile"
                                  height="140"
                                  image={data.thumbnailsHight}
                                  title="Contemplative Reptile"
                                  />
                              :
                                  <Skeleton variant="rect" width={210} height={118} />
                              }
                                <CardContent>
                                  {data?
                                    <Typography gutterBottom variant="h6" component="h5">
                                        {data.title || <Skeleton />}
                                    </Typography>
                                  :
                                  <Skeleton animation="wave" />
                                  }
                                  {data?
                                    <Typography variant="body2" color="textSecondary" component="p">
                                        {data.channelTitle || <Skeleton />}
                                    </Typography>
                                  :
                                  <Skeleton animation="wave" />
                                  }
                                {/* <Row>
                                  <Col>
                                    <Row>
                                      <Col xs={3}>
                                          <Typography variant="body2" color="textSecondary" component="p">
                                              <NumericLabel params={option}>{data.viewCount}</NumericLabel> 
                                          </Typography>
                                      </Col>
                                      <Col xs={3}>
                                        <Typography variant="body2" color="textSecondary" component="p">
                                              views
                                          </Typography>
                                      </Col>
                                    </Row>

                                  </Col>
                                  <Col>
                                  <Typography variant="body2" color="textSecondary" component="p">
                                        <Moment fromNow>{data.publishedAt}</Moment>
                                  </Typography>
                                  </Col>
                                </Row> */}
                                </CardContent>
                            </div>
                        </div>
                    </Col>
                    ))
                  } 
                </Row>
            </div>
            <div>
              <Recent />
            </div>
                <SnackbarProvider maxSnack={3}>
                    <Error value={this.props.error}/>
                    {isOnline
                    ?""
                    :<Error value={"Check  your Internet Connection"}/>
                    }
                </SnackbarProvider>
          </div>
        )
    }
}

const mapStateToProps = (state) => ({
    isLoading: state.youtube.isLoading,
    ydata: state.youtube.ydata,
    error: state.auth.error,
  });

export default connect(mapStateToProps, {loadYData})(RecentVideos)
