import React, { Component } from 'react'
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import NumericLabel from 'react-pretty-numbers'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import {Row, Col} from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import { loadYData, searchData } from '../../store/actions/youtube';
import LinesEllipsis from 'react-lines-ellipsis'
import {withRouter} from 'react-router';
import ReactPlayer from 'react-player'
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';
import axios from 'axios';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';

const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': false,
    'title': true
    };

let watch;


export class Watch extends Component {
    state={
        posts:[]
    }
    componentDidMount(){
        this.props.loadYData()
        let str = this.props.location.search
        watch  = str.split('=')[1]
        if(this.props.location.search){
            if(str.split('=')[1]){
                this.props.searchData(str.split('=')[1])  
            } else{
                this.props.history.push("/");
            } 
        }
        else if(!this.props.location.search){
            this.props.history.push("/");
        }

    }
    render() {
        const BASE_URL = `https://www.youtube.com/watch?v=${watch}`;
        {console.log(BASE_URL)}
        return (
            <div style={{ height: '100%', width: '100%' }}>
                <Row>
                    <Col xs={10}>
                    </Col>
                    <Col xs={2}>
                    </Col>
                </Row>

            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    searchdata: state.youtube,
    data: state.youtube.ydata
  });

export default connect(mapStateToProps,{loadYData, searchData})(withRouter(Watch));
