import React, { Component } from 'react'
import TextTruncate from 'react-text-truncate'; // recommend
import Typography from '@material-ui/core/Typography';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import { Divider, Grid } from '@material-ui/core';
export default class AllStories extends Component {
    render() {
        const calendarStrings = {
            lastDay : '[Yesterday at] LT',
            sameDay : '[Today at] LT',
            nextDay : '[Tomorrow at] LT',
            lastWeek : '[last] dddd [at] LT',
            nextWeek : 'dddd [at] LT',
            sameElse : 'L'
        };
        return (
            <div>
                <div>
                    {this.props.value.isLoading
                    ?
                    ""
                    :
                    <div style={{lineHeight: '60px', display: 'flex' , margin: '10px' }}>
                        <div style={{ marginLeft: '10px', lineHeight: '20px' }}>
                            <Typography variant="h6" gutterBottom style={{ marginBottom: '0' }}>
                            Latest Blogs 
                            </Typography>
                            <Typography variant="caption" gutterBottom>
                            Punithargal Saints
                            </Typography>                                                   
                        </div>
                    </div>
                    }
                </div>
                <Grid container direction={'column'} spacing={0}>
                    {(this.props.value.isLoading ? Array.from(new Array(8)) : (this.props.value.bdata?this.props.value.bdata:"")).map((data, index) => (
                        <Grid  style={{cursor: 'pointer'}} onClick={e => this.props.history.push(`/view/${data.id}`)}> 
                            <Divider />
                            <div style={{width: "100%", maxWidth: '1280px', margin: '0 auto',  marginTop: '23px'}}    >
                                <img src="https://dynaimage.cdn.cnn.com/cnn/c_fill,g_auto,w_1200,h_675,ar_16:9/https%3A%2F%2Fcdn.cnn.com%2Fcnnnext%2Fdam%2Fassets%2F191120053137-03-milky-way-images-australia.jpg" style={{ width: '150px', height: '70px', float: 'right', }}  />
                            </div>
                            <Typography variant="h5" gutterBottom>

                            <Moment>
                                {data?data.created_on:""}
                            </Moment>
                            </Typography>
                            <Typography variant="h6" gutterBottom>
                                {data?data.title:""}
                            </Typography>
                        </Grid>
                    ))}
            </Grid>
            </div>
        )
    }
}
