import React, { Component } from 'react';
import {loadYData} from '../../store/actions/youtube'; 
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import store from '../../store/configureStore';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/styles';
import {Row, Col} from 'react-bootstrap'
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });


export class RecentVideos extends Component {
    static propTypes = {
        loadYData: PropTypes.func.isRequired,
        isLoading: PropTypes.bool,
        ydata:  PropTypes.array,
        loading: PropTypes.bool,
      };

      componentDidMount(){
        this.props.loadYData()
        console.log(this.props)
      }

    render() {
        const le  = this.props.ydata
        const l2 = le?le.length:""
        const loading = this.props;
        return (
            <Row>
                {this.props.ydata?this.props.ydata
                .map((data)=>(
                <Col xs={2} key={data.id}>
                    <div style={{ maxWidth: '325px'}} >
                        <div>
                        <Box key={data} width={210} marginRight={0.5} my={5}>
                            <img style={{ width: 210, height: 118 }} alt={data.title} src={data.thumbnailsHight} />
                        </Box>
                        <Box pr={2}>
                        <Typography gutterBottom variant="body2">
                          {data.title}
                        </Typography>
                        <Typography display="block" variant="caption" color="textSecondary">
                          {data.position}
                        </Typography>
                        <Typography variant="caption" color="textSecondary">
                                <Moment toNow>{data.publishedAt }</Moment>
                        </Typography>
                      </Box>
                        </div>
                    </div>
                </Col>
                )):
                <Grid container wrap="nowrap">
                {(loading ? Array.from(new Array(l2)) : this.props.ydata).map((item, index) => (
                  <Box key={index} width={210} marginRight={0.5} my={5}>
                    {item ? (
                      <img style={{ width: 210, height: 118 }} alt={item.title} src={item.src} />
                    ) : (
                      <Skeleton variant="rect" width={210} height={118} />
                    )}

                    {item ? (
                      <Box pr={2}>
                        <Typography gutterBottom variant="body2">
                          {item.title}
                        </Typography>
                        <Typography display="block" variant="caption" color="textSecondary">
                          {item.channel}
                        </Typography>
                        <Typography variant="caption" color="textSecondary">
                          {`${item.views} â€¢ ${item.createdAt}`}
                        </Typography>
                      </Box>
                    ) : (
                      <Box pt={0.5}>
                        <Skeleton />
                        <Skeleton width="60%" />
                      </Box>
                    )}
                  </Box>
                ))}
              </Grid>
              } 
            </Row>

        )
    }
}

const mapStateToProps = (state) => ({
    isLoading: state.youtube.isLoading,
    ydata: state.youtube.ydata,
  });

export default connect(mapStateToProps, {loadYData})(RecentVideos)
