import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme, fade } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import axios from 'axios';
import { Form, FormControl,Button } from 'react-bootstrap';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import NotificationsIcon from '@material-ui/icons/Notifications';
import {isMobile} from 'react-device-detect';
import './AppBar.css'
import HomeIcon from '@material-ui/icons/Home';
import AppsIcon from '@material-ui/icons/Apps';
import EventIcon from '@material-ui/icons/Event';
import VideoLibraryIcon from '@material-ui/icons/VideoLibrary';
import WhatshotIcon from '@material-ui/icons/Whatshot';
import MusicVideoIcon from '@material-ui/icons/MusicVideo';
import PollIcon from '@material-ui/icons/Poll';
import ViewQuiltIcon from '@material-ui/icons/ViewQuilt';
import Tooltip from '@material-ui/core/Tooltip';
import { useSelector } from 'react-redux'
import { NavDropdown } from 'react-bootstrap'
import { useHistory } from "react-router-dom";
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';
import { Row, Col } from 'react-bootstrap';
import MenuItem from '@material-ui/core/MenuItem';
import { withStyles } from '@material-ui/core/styles';
import SettingsIcon from '@material-ui/icons/Settings';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import { logout } from '../../store/actions/auth';

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
    whiteSpace: 'nowrap',
  },
  drawerOpen: {
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerClose: {
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    overflowX: 'hidden',
    width: theme.spacing(7) + 1,
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9) + 1,
    },
  },
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));


export function MiniDrawer(props) {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const store = useSelector(state => state)
  console.log(props)
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  let history = useHistory();
  function loginNav() {
    history.push("/account/login");
  }
  function handleLogout(){

  }
  const handleDrawerClose = () => {
    setOpen(false);
  };

  const StyledMenuItem = withStyles((theme) => ({
    root: {
      '&:focus': {
        backgroundColor: theme.palette.primary.main,
        '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
          color: theme.palette.common.white,
        },
      },
    },
  }))(MenuItem);
  let cusCon;
  if(store.auth.isAuthenticated){
    cusCon = (
      <div style={{ width: '-webkit-fill-available' }}>
        <Tooltip title="Accounts" placement="down">
        <NavDropdown title={<AccountCircle />} style={{ textDecorationLine: "transparent", top: '3px' ,float: 'right' }} id="nav-dropdown">
            <Card style={{ background: '#ffffff',  boxShadow: 'none', width: '280px' }}>
              <CardContent style={{  }}>
                <Row>
                  <Col xs={3}>
                    <Avatar style={{ height: '40px',  width:  '40px'  }}>{store.auth.user?store.auth.user.first_name.slice(0,1).toUpperCase():""}</Avatar>
                  </Col>
                  <Col xs={9}>
                    <Typography style={{  alignContent: 'right', justifyContent: 'right', display: 'flex'  }} variant="h6" noWrap>
                        {store.auth.user?store.auth.user.first_name+store.auth.user.last_name:"UserName"}
                    </Typography> 
                    <Typography style={{ color: '#333333',  fontSize: '11px' , alignContent: 'right', justifyContent: 'right', display: 'flex'  }} variant="h6" noWrap>
                        {store.auth.user?store.auth.user.email:"Email"}
                    </Typography> 
                  </Col>
                </Row>
              </CardContent>
              <CardContent style={{ alignContent: 'center', justifyContent: 'center', display: 'flex', marginTop: '30px' }}>
                              <Button onClick={logout} variant="outlined" style={{ fontWeight: '400', outline: '0'  }}>Sign out of Drive</Button>             
              </CardContent> 
              <Divider />
              <StyledMenuItem>
                <ListItemIcon>
                  <SettingsIcon fontSize="small" />
                </ListItemIcon>
                <ListItemText primary="Account Settings" />
              </StyledMenuItem>
              <IconButton onClick={handleLogout}>
                <ListItemIcon>
                    <ExitToAppIcon fontSize="small" />
                </ListItemIcon>
                <ListItemText primary="Signout" />
              </IconButton>
            </Card>
        </NavDropdown>
        </Tooltip>
        <Tooltip title="Notification" placement="down">
        <NavDropdown title={<NotificationsIcon />} id="nav-dropdown" style={{ textDecorationLine: "transparent", top: '3px' , boxShadow: '30px solid  #eeee',float: 'right' }}>

        </NavDropdown>
        </Tooltip>
     </div>
    )
  }
  else{
    cusCon = (
      <Button  onClick ={loginNav}>Signin</Button>
    )
  }
  return (
    <div className={classes.root}>
      <CssBaseline />
     <AppBar
        position="fixed"
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
        elevation={0}
      >

        <Toolbar style={{ backgroundColor: '#ffffff'}} >
          <IconButton
            color="#606060"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            className={clsx(classes.menuButton, {
              [classes.hide]: open,
            })}
          >
            <MenuIcon />

          </IconButton>
          <img src="http://punithargal.org/images/logo-alt-1.png" style={{ height: '30px', width: '30px' }}/>
          <Form inline style={{ width: '150%' }}>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" style={{ width: '450px',  marginRight: '0px !important', marginLeft: '200px' }}/>
            <Button><SearchIcon /></Button>
         </Form>
         {cusCon}
        </Toolbar>

      </AppBar>
      <Drawer
        variant="permanent"
        className={clsx(classes.drawer, {
          [classes.drawerOpen]: open,
          [classes.drawerClose]: !open,
        })}
        classes={{
          paper: clsx({
            [classes.drawerOpen]: open,
            [classes.drawerClose]: !open,
          }),
        }}
      >
        <div className={classes.toolbar}>
            <div style={{ width: '100px' }}>
            Punithargal
            </div>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'rtl' ? <MenuIcon /> : <MenuIcon />}
          </IconButton>
        </div>
        <Divider />
        <List>
        <Tooltip title="Home" placement="right">
          <ListItem button >
                <ListItemIcon><HomeIcon /></ListItemIcon>
                <ListItemText >Home</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Essay" placement="right">
          <ListItem button >
              <ListItemIcon><AppsIcon /></ListItemIcon>
              <ListItemText >Essay</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Calender" placement="right">
          <ListItem button >
              <ListItemIcon><EventIcon /></ListItemIcon>
              <ListItemText >Calender</ListItemText>
          </ListItem>
        </Tooltip>
        </List>
        <Divider />
        <List>
        <Tooltip title="Library" placement="right">
          <ListItem button >
              <ListItemIcon><VideoLibraryIcon /></ListItemIcon>
              <ListItemText >Library</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Trending Videos" placement="right">
          <ListItem button >
                <ListItemIcon><WhatshotIcon /></ListItemIcon>
                <ListItemText >Trending Videos</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Recent Videos" placement="right">
          <ListItem button >
              <ListItemIcon><MusicVideoIcon /></ListItemIcon>
              <ListItemText >Recent Videos</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Popular Saints" placement="right">
          <ListItem button >
              <ListItemIcon><PollIcon /></ListItemIcon>
              <ListItemText >Popular Saints</ListItemText>
          </ListItem>
        </Tooltip>
        <Tooltip title="Mother Mary" placement="right">
          <ListItem button >
              <ListItemIcon><ViewQuiltIcon /></ListItemIcon>
              <ListItemText >Mother Mary</ListItemText>
          </ListItem>
        </Tooltip>
        </List>
      </Drawer>
      <main className={classes.content}>
        <div className={classes.toolbar} />
        <Typography paragraph>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt
          ut labore et dolore magna aliqua. Rhoncus dolor purus non enim praesent elementum
          facilisis leo vel. Risus at ultrices mi tempus imperdiet. Semper risus in hendrerit
          gravida rutrum quisque non tellus. Convallis convallis tellus id interdum velit laoreet id
          donec ultrices. Odio morbi quis commodo odio aenean sed adipiscing. Amet nisl suscipit
          adipiscing bibendum est ultricies integer quis. Cursus euismod quis viverra nibh cras.
          Metus vulputate eu scelerisque felis imperdiet proin fermentum leo. Mauris commodo quis
          imperdiet massa tincidunt. Cras tincidunt lobortis feugiat vivamus at augue. At augue eget
          arcu dictum varius duis at consectetur lorem. Velit sed ullamcorper morbi tincidunt. Lorem
          donec massa sapien faucibus et molestie ac.
        </Typography>
        <Typography paragraph>
          Consequat mauris nunc congue nisi vitae suscipit. Fringilla est ullamcorper eget nulla
          facilisi etiam dignissim diam. Pulvinar elementum integer enim neque volutpat ac
          tincidunt. Ornare suspendisse sed nisi lacus sed viverra tellus. Purus sit amet volutpat
          consequat mauris. Elementum eu facilisis sed odio morbi. Euismod lacinia at quis risus sed
          vulputate odio. Morbi tincidunt ornare massa eget egestas purus viverra accumsan in. In
          hendrerit gravida rutrum quisque non tellus orci ac. Pellentesque nec nam aliquam sem et
          tortor. Habitant morbi tristique senectus et. Adipiscing elit duis tristique sollicitudin
          nibh sit. Ornare aenean euismod elementum nisi quis eleifend. Commodo viverra maecenas
          accumsan lacus vel facilisis. Nulla posuere sollicitudin aliquam ultrices sagittis orci a.
        </Typography>
      </main> 
    </div>
  );
}


export default MiniDrawer
