import React, { useState, useEffect, Component } from 'react';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import NumericLabel from 'react-pretty-numbers'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import {Row, Col} from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import { searchData } from '../../store/actions/youtube';
import LinesEllipsis from 'react-lines-ellipsis'
import {withRouter} from 'react-router';
import CircularProgress from '@material-ui/core/CircularProgress';
import Grid from '@material-ui/core/Grid';
import { Container } from '@material-ui/core';


const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };



export class List extends Component {

    constructor(props){
        super(props);
        this.state={
            isFetching:false,
            setIsFetching: false,
            listItems:Array.from(Array(30).keys(), n => n + 1),
            setListItems:Array.from(Array(30).keys(), n => n + 1),
        }
    }
    componentDidMount(){
        let str = this.props.location.search
        if(this.props.location.search){
            if(str.split('=')[1]){
                console.log("hey louji")
                this.props.searchData(str.split('=')[1])  
            } else{
                this.props.history.push("/");
            } 
        }
        else if(!this.props.location.search){
            this.props.history.push("/");
        }
    }

    render() {
        return (
         <div style={{ width:"1000px" }}>
              {this.props.searchdata.ysearchdata ? this.props.searchdata.ysearchdata
                .map((data)=>(
                    <Grid container> 
                        <Grid item  xs={3.8}>
                            <div style={{ width: '260px', height: '200px' }}>
                                <CardMedia
                                component="img"
                                alt={data.title}
                                height="140"
                                image={data.thumbnailsHight}
                                title="Contemplative Reptile"
                                />
                            </div>
                        </Grid>
                        <Grid item  xs={8}>
                        <CardContent  style={{ padding: '0' }}>
        <Typography gutterBottom variant="h6" component="h5">
            {data.title}
        </Typography>
        <LinesEllipsis
        text={data.description}
        maxLine='4'
        ellipsis='......'
        trimRight
        basedOn='letters'
        />
        <Row>
    <Col style={{ marginTop: '20px' }}>
        <Row xs={3}>
        <Col xs={1}>
            <Typography variant="body2" color="textSecondary" component="p">
                <NumericLabel params={option}>{data.viewCount}</NumericLabel> 
            </Typography>
        </Col>
        <Col xs={2}>
            <Typography variant="body2" color="textSecondary" component="p">
                views
            </Typography>
        </Col>
        </Row>

    </Col>
    <Col  style={{ marginTop: '20px' }}>
    <Typography variant="body2" color="textSecondary" component="p">
        Published  <Moment fromNow>{data.publishedAt}</Moment>
    </Typography>
    </Col>
    </Row>
    </CardContent>          
                        </Grid>
                    </Grid>
                )):
                <CircularProgress />
                }
         </div>
        )
    }
}

const mapStateToProps = (state) => ({
    searchdata: state.youtube
  });

export default connect(mapStateToProps,{searchData})(withRouter(List));
