import React, { useState, useEffect, Component } from 'react';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Moment from 'react-moment';
import Skeleton from '@material-ui/lab/Skeleton';
import NumericLabel from 'react-pretty-numbers'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import {Row, Col} from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import { searchData } from '../../store/actions/youtube';
import LinesEllipsis from 'react-lines-ellipsis'
import {withRouter} from 'react-router';
import CircularProgress from '@material-ui/core/CircularProgress';
import Grid from '@material-ui/core/Grid';
import { Container, Divider } from '@material-ui/core';
import TextTruncate from 'react-text-truncate'; // recommend
import InfiniteScroll from "react-infinite-scroll-component";
import { render } from "react-dom";

const option = {
    'justification': 'L',
    'locales': 'en-US',
    'currency':false,
    'percentage': false,
    'wholenumber':'ceil',
    'commafy': true,
    'cssClass': ['red'],
    'shortFormat': true,
    'shortFormatMinValue': 10000,
    'title': true
    };

    const style = {
        height: 30,
        border: "1px solid green",
        margin: 6,
        padding: 8
      };


export class List extends Component {

    constructor(props){
        super(props);
    }
    state = {
        items: Array.from({ length: 10 }),
        hasMore: true
      };
      fetchMoreData = () => {
        if (this.state.items.length >= 500) {
          this.setState({ hasMore: false });
          return;
        }
        setTimeout(() => {
          this.setState({
            items: this.state.items.concat(this.props.searchdata.ysearchdata)
          });
        }, 500);
      };
    componentDidMount(){
        let str = this.props.location.search
        if(this.props.location.search){
            if(str.split('=')[1]){
                console.log("hey louji")
                this.props.searchData(str.split('=')[1])  
            } else{
                this.props.history.push("/");
            } 
        }
        else if(!this.props.location.search){
            this.props.history.push("/");
        }
    }

    render() {
        {console.log(this.props)}
        return (
         <div style={{ width:"1000px", marginLeft: this.props.DrawerStateOpen?"50px":"120px",marginTop: '50px' }} id="scrollableDiv">
             <Typography variant="body1" style={{ margin: '10px', color: 'currentColor', textDecoration: '' }}>
                 Search for the term <a href="#">{this.props.location.search.split('=')[1]}</a> : {this.props.searchdata.ysearchdata ? this.props.searchdata.ysearchdata.length:""}  result found
             </Typography>
             <Divider style={{  }}/>

             <div >
                    <h1>demo: react-infinite-scroll-component</h1>
                    <hr />
                    <InfiniteScroll
                    dataLength={this.state.items.length}
                    next={this.fetchMoreData}
                    hasMore={this.state.hasMore}
                    loader={<h4>Loading...</h4>}
                    scrollableTarget="scrollableDiv"
                    endMessage={
                        <p style={{ textAlign: "center" }}>
                        <b>Yay! You have seen it all</b>
                        </p>
                    }
                    >
                    {console.log(this.state.items)}

                    {this.state.items.map((i, index) => (
                        <div style={style} key={index}>
                        div - #
                        </div>
                    ))}
                    </InfiniteScroll>
            </div>
         </div>
        )
    }
}

const mapStateToProps = (state) => ({
    searchdata: state.youtube
  });

export default connect(mapStateToProps,{searchData})(withRouter(List));
