import React, { Component } from 'react'
import axios from 'axios'
import { Button, Comment, Form, Header } from 'semantic-ui-react'
import './Comments.css';
import Avatar from '@material-ui/core/Avatar';
import { Divider } from '@material-ui/core';

export default class Comments extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: []
    }
  }

    componentDidMount(){
        axios.get(`https://www.googleapis.com/youtube/v3/commentThreads?part=snippet%2Creplies&maxResults=1000&videoId=${this.props.value}&key=AIzaSyBqXFMg2KDNkXlhPU27w1Zcphm00su7cI0`)
        .then((response) => {
          console.log(typeof(response.data.items))
            this.setState(()=>{
              return{data: response.data.items}
            })
        })
        .catch((error) => {
            console.log(error);
        });
    }
    render() {
      console.log("this.state.data")
      console.log(this.state.data)
        return (
          <div style={{ margin: '10px' }}>
          <Header as='h3' dividing>
            Comments
          </Header>
          <Divider style={{ width: '100px' }}/>
            <div style={{ height: "100px", lineHeight: '100px', display: 'flex'  }}>
                <div>
                    <Avatar src="http://punithargal.org/images/logo-alt-1.png" />
                </div>
                <div>
                    <h3 style={{fontWeight: '500', verticalAlign: 'middle', marginLeft: '20px'}}>
                            Punithargal Saints
                    </h3>                                                        
                </div>

            </div>
          </div>
        )
    }
}
