import React, { Component } from 'react'
import Toolbar from '@material-ui/core/Toolbar';
import Link from '@material-ui/core/Link';
import { Divider } from '@material-ui/core';
import {Container,Image,  Row, Col}  from 'react-bootstrap'
import Typography from '@material-ui/core/Typography';
import {loadBlog} from '../../store/actions/blog';
import { connect } from 'react-redux';

export  class Blog extends Component {
    componentDidMount(){
        this.props.loadBlog()
        console.log(this.props.bdata)
      }
    render() {
        return (
            <Container style={{width: "100%", maxWidth: '1280px', margin: '0 auto'}}>
            <div>
                <Divider />
                <div>
                    <div style={{ margin: '10px' }}>
                        <h1>Stories</h1>
                    </div>
                    <Row>
                        {this.props.bdata?this.props.bdata.slice(0,6).map((data)=>{
                            return(
                                <Col xs={3} style={{margin:  '30px', cursor: 'pointer'}} onClick={e => this.props.history.push(`/view/${data.id}`)} >
                                <div style={{ width: '350px'}}>
                                    <Typography variant="h5" gutterBottom>
                                        {data.title}
                                    </Typography>
                                    <Typography variant="h6" gutterBottom>
                                        {data.meta_description}
                                    </Typography>
                                    <Image src="https://dynaimage.cdn.cnn.com/cnn/c_fill,g_auto,w_1200,h_675,ar_16:9/https%3A%2F%2Fcdn.cnn.com%2Fcnnnext%2Fdam%2Fassets%2F191120053137-03-milky-way-images-australia.jpg" style={{ width: '100%' }} rounded />
                                </div>
                            </Col>
                            )
                        }):""}
                    </Row>
                </div>
               <div>
                    <div style={{ margin: '10px' }}>
                        <h1>All Stories</h1>
                    </div>
                    {this.props.bdata?this.props.bdata.slice(0,6).map((data)=>{
                        return(
                        <div  style={{cursor: 'pointer'}} onClick={e => this.props.history.push(`/view/${data.id}`)}> 
                            <Divider />
                            <Container style={{width: "100%", maxWidth: '1280px', margin: '0 auto',  marginTop: '23px'}}    >
                                <Image src="https://dynaimage.cdn.cnn.com/cnn/c_fill,g_auto,w_1200,h_675,ar_16:9/https%3A%2F%2Fcdn.cnn.com%2Fcnnnext%2Fdam%2Fassets%2F191120053137-03-milky-way-images-australia.jpg" style={{ width: '150px', height: '70px', float: 'right', }}  />
                            </Container>
                            <Typography variant="h5" gutterBottom>
                                May 21 / Maps
                            </Typography>
                            <Typography variant="h3" gutterBottom>
                                {data.title}
                            </Typography>
                        </div>
                        )
                    }):""}
               </div>
            </div>
            </Container>
        )
    }
}

const mapStateToProps = (state) => ({
    bdata: state.blog.bdata
  });



export default connect(mapStateToProps, {loadBlog})(Blog)
